    private void FConvertionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FConvertionActionPerformed
         // Convert F to C
        String fConvert = FDegree.getText();
       
        double FDegree = Integer.parseInt(fConvert);
         
         double fAnswer = (FDegree -32) * 0.5556 ;
         CDegree.setText(""+fAnswer);
    }//GEN-LAST:event_FConvertionActionPerformed

