    private void CConvertionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CConvertionActionPerformed
        // Convert F to C
        String cConvert = CDegree.getText();
       
        double CDegree = Integer.parseInt(cConvert);
         
         double cAnswer = CDegree * 1.8 +32 ;
         FDegree.setText(""+cAnswer);
    }//GEN-LAST:event_CConvertionActionPerformed

